mapper.select:
SELECT ID_  AS TASK_ID, NAME_ AS NODE_NAME, TASK_DEF_KEY_ AS NODE_KEY,ASSIGNEE_,PROC_INST_ID_  AS PROC_INST_ID
FROM MES1.ACT_RU_TASK WHERE 1=1

  	AND PROC_INST_ID_ = #{processInstanceId}


  	AND ID_ = #{taskId}

SELECT DISTINCT TA.ID_ AS TASK_ID, TA.NAME_ AS NODE_NAME, TA.TASK_DEF_KEY_ AS NODE_KEY, TA.ASSIGNEE_, TA.PROC_INST_ID_ FROM MES1.ACT_RU_TASK TA LEFT JOIN MES1.ACT_RU_IDENTITYLINK I ON TA.PROC_INST_ID_ = I.PROC_INST_ID_ OR TA.ID_ = I.TASK_ID_ WHERE I.USER_ID_ = '${F_USE_CD}'
AND TA.PROC_INST_ID_ = #{processInstanceId}
AND TA.ID_ = #{taskId}

mapper.insert:
INSERT INTO MES1.R_MES_BUSI_DEF_T (BUSI_CODE,BUSI_NAME,PROCDEF_KEY,PRIO_LVL,BUSI_SCOPE,HIST_URL,BATCH_YN,ENABLE_ALLDAY,MO,USR_DFN_1,USR_DFN_2,USR_DFN_3,USR_DFN_4,USR_DFN_5,CRT_ID,CRT_IP,CRT_DT) (
SELECT '${item.BUSI_CODE}','${item.BUSI_NAME}','${item.PROCDEF_KEY}','${item.PRIO_LVL}','${item.BUSI_SCOPE}','${item.HIST_URL}', '${item.BATCH_YN}','${item.ENABLE_ALLDAY}','${item.MO}','${item.USR_DFN_1}','${item.USR_DFN_2}','${item.USR_DFN_3}','${item.USR_DFN_4}','${item.USR_DFN_5}','${F_USE_CD}','${CRT_IP}',SYSDATE FROM DUAL
)
